					if (substr($line,-1)==";") {
						$query.=" ".$line;
						$wpdb->query($query);
						$query = "";
					} else {
						$query.=" ".$line;
					}
				}

				$wp_rewrite->flush_rules();
				wp_cache_flush();
		}